SELECT HACKERS.HACKER_ID, NAME FROM SUBMISSIONS JOIN CHALLENGES ON SUBMISSIONS.CHALLENGE_ID = CHALLENGES.CHALLENGE_ID JOIN DIFFICULTY ON CHALLENGES.DIFFICULTY_LEVEL = DIFFICULTY.DIFFICULTY_LEVEL JOIN HACKERS ON SUBMISSIONS.HACKER_ID = HACKERS.HACKER_ID WHERE SCORE = MAX_SCORE GROUP BY HACKERS.HACKER_ID, NAME ORDER BY COUNT(*) DESC, HACKERS.HACKER_ID; 